package io.github.thanktoken.core.impl.v001;

import java.util.Arrays;
import java.util.Collections;
import java.util.List;

import net.sf.mmm.security.api.crypt.asymmetric.Rsa;
import net.sf.mmm.security.api.key.asymmetric.SecurityAsymmetricKeyPair;

/**
 * {@link Rsa} {@link SecurityAsymmetricKeyPair key pairs} for testing.
 */
public interface TestKeyPairs {

  /** {@link Rsa} with {@link Rsa#keyLength4096()}. */
  Rsa RSA_4096 = Rsa.keyLength4096();

  /** Test {@link SecurityAsymmetricKeyPair}. */
  SecurityAsymmetricKeyPair KEY_PERSON_1 = RSA_4096.createKeyPair(
      "",
      "");

  /** Test {@link SecurityAsymmetricKeyPair}. */
  SecurityAsymmetricKeyPair KEY_PERSON_2 = RSA_4096.createKeyPair(
      "",
      "");

  /** Test {@link SecurityAsymmetricKeyPair}. */
  SecurityAsymmetricKeyPair KEY_PSEUDONYM = RSA_4096.createKeyPair(
      "",
      "");

  /** Test {@link SecurityAsymmetricKeyPair}. */
  SecurityAsymmetricKeyPair KEY_COUNTRY_DE = RSA_4096.createKeyPair(
      "",
      "");

  /** Test {@link SecurityAsymmetricKeyPair}. */
  SecurityAsymmetricKeyPair KEY_ORGANIZATION_1 = RSA_4096.createKeyPair(
      "",
      "");

  /** Test {@link SecurityAsymmetricKeyPair}. */
  SecurityAsymmetricKeyPair KEY_SUSTAINANILITY_1 = RSA_4096.createKeyPair(
      "",
      "");

  /** Test {@link SecurityAsymmetricKeyPair}. */
  SecurityAsymmetricKeyPair KEY_STATE = RSA_4096.createKeyPair(
      "",
      "");

  /** Test {@link SecurityAsymmetricKeyPair}. */
  SecurityAsymmetricKeyPair KEY_COMMUNITY = RSA_4096.createKeyPair(
      "",
      "");

  /** A {@link List} with all above {@link SecurityAsymmetricKeyPair}s. */
  List<SecurityAsymmetricKeyPair> KEYS = Collections.unmodifiableList(
      Arrays.asList(KEY_PERSON_1, KEY_PERSON_2, KEY_PSEUDONYM, KEY_COUNTRY_DE, KEY_ORGANIZATION_1, KEY_SUSTAINANILITY_1, KEY_STATE, KEY_COMMUNITY));

}
